int main()
{
	GRRLIB_Init();
	GRRLIB_Settings.antialias = false;
	PAD_Init();
	
	LevelSelector* LS = new LevelSelector();
	LS->currentLevel = LS->gameScreen;
	
	Player player;

	while (1)
	{
		GRRLIB_FillScreen(GRRLIB_BLACK);
		PAD_ScanPads(); // Scans for a controller pad
		
		LS->LevelSwitcher(MapCollider, MapGraphics);		
		
		GRRLIB_Render();
	}

	LS->Textures();
	
	GRRLIB_Exit();
	delete LS;
	exit(0); //Used by STDLib
}